-- create table 
drop table if exists  category;
create table category (
	id varchar(4) not null,
    category_name nvarchar(150) default 'default',
    category_slug varchar(40)  unique not null,
    p_category_id varchar(4)  references category(id) ,
    
    primary key (id)
); 
