-- Procedure 
DROP PROCEDURE IF EXISTS proc_getCategories;
delimiter //
CREATE PROCEDURE proc_getCategories()
begin
	
	SELECT json_arrayagg( JSON_OBJECT(
		'id', c.id, 
        'category_name', c.category_name, 
        'category_slug', c.category_slug,
       'sub_category' ,(select  json_arrayagg(
				JSON_OBJECT(
						'id', sc.id, 
						'category_name', sc.category_name, 
						'category_slug', sc.category_slug
				) 
        ) from  category sc where sc.p_category_id = c.id )
        )) 'data'  from  category c where c.p_category_id = 'c01';
       
end//
delimiter ;


DROP PROCEDURE IF EXISTS proc_getProducts;
delimiter //
CREATE PROCEDURE proc_getProducts()
begin
	SELECT  p.pId,title,slug,description, price, sold, category_id, ( 
			SELECT JSON_ARRAYAGG(
				JSON_OBJECT(
					'url',url,
					'thumbUrl', thumbUrl,
                    'itemId',itemId,
                    'colorName',colorName,
                    'colorCode',colorCode,
                    'inventory', (SELECT JSON_ARRAYAGG(
						JSON_OBJECT(
							'itemId', inventory.itemId,
                            'pId', p.pId,
							'sizeId',s.sizeId,
                            'sizeName', s.sizeName,
                            'quantity', inventory.quantity
                        )) from inventory join size s on inventory.sizeId = s.sizeId where  inventory.itemId = i.itemId
                    )
				)
			)  from item i where  i.pId = p.pId
        ) 'collections' from product p order by category_id ;
end//
delimiter ;
